  useEffect (() =>{

    fetch('http://localhost:8000/vieworder').then(
      response => response.json()
      ).then(data => {
        setItems({list : data})

    })}, [])

  const handleButtonClick = (e, id) => {
    // e.preventDefault();
    console.log(id);
    setDetails(true);

    }
