  return (

    <>
    {(items.list.map((item, index) => {
      return (
        <div key = {index}>
        <h3>{item.table.id}  {item.table.tableName}</h3>
        <button onClick = {(e) => handleButtonClick(e,item.id)}>View/Edit</button>

        </div>
      )


      }))}
      </>

  );}
  return(
    <>
    <ViewDetail item = "dg"/>
    </>
  )

}


