import './App.css';
import React, {useState, useEffect} from 'react';

const App = () =>  {


  const [items, setItems] = useState({list : []});

  useEffect (() =>{

    fetch('http://localhost:8000/menu').then(
      response => response.json()
      ).then(data => {

        setItems({list : data})

    })}, [])






  return (
    <div className="App">

      <div><h1>Item</h1></div> 
  {(items.list.map((item, index) => {
        return(
          <div key = {index}>

            <h3>{item.itemName} {item.price}</h3>

           </div>
        )
      }))} 

    </div>
  );
  }


export default App;
