import React, {useEffect, useState} from 'react';
import { useAlert } from 'react-alert'

const AddOrder = () => {
  const [table, setTable] = useState({tables : []})
  const [items, setItems] = useState({foods : []})
  const [inputList, setInputList] = useState([{ items: "", quantity: "" }]);


  useEffect(() => {

    fetch('http://localhost:8000/menu').then(
      response => response.json()
      ).then(data => {
        setItems({foods : data})

    });

    fetch('http://localhost:8000/tables').then(
      response => response.json()
      ).then(data => {
        setTable({tables : data})

    })

  }, [])


  const handleInputChange = (e, index) => {
    const { name, value } = e.target;
    const list = [...inputList];
    list[index][name] = value;
    setInputList(list);
  };

  const handleAddClick = () => {
    setInputList([...inputList, { items: "", quantity: "" }]);
  };

  const handleSubmit = (e) => {
    e.preventDefault()

    let url = 'http://localhost:8000/createorder'

    console.log(e.target.items.value.itemName);

    fetch (url, {
      method : 'POST',
      headers : {
        'Content-type' : 'application/json',
      },
      body : JSON.stringify({
        'tid': e.target.table.value,
        'OrderedItem': [
            {'itemid': e.target.items.value, 'quantity': e.target.quantity.value},

        ],
    })
    }).then ((response) => {
      alert.show('Task was sucessful !!', {type:'success'})
    }).catch ((error) => {
      alert.show('There was an error !!!', {type : 'error'})
    })
  }
  const alert = useAlert()

  return (
    // <>

    // <form onSubmit = {handleSubmit} >



    //   <input type = "number" name = "quantity"/>


    //   <input type = 'submit' />
    // </form>
    // </>
<>

  <select name='table' value= "">
        {table.tables.map(tab =>
          <option key={tab.id} value={tab.id} >{tab.tableName}</option>
          )};
      </select>

    {inputList.map((x, i) => {
      return (
        <div className="box">
          <select name = "items" onChange = {e => handleInputChange(e, i)} value = {x.items}>
            {items.foods.map(food =>
              <option key = {food.id} value = {food.id}> {food.itemName} </option>
            )}
          </select>


          <div className="btn-box">

            {inputList.length - 1 === i && <button onClick={handleAddClick}>Add</button>}
          </div>
        </div>
      );
    })}
    <div style={{ marginTop: 20 }}>{JSON.stringify(inputList)}</div>

  </>

  );

}

export default AddOrder;
