import './App.css';
import React, {useState, useEffect} from 'react';
import Order from './components/order';

const App = () =>  {


  const [items, setItems] = useState({list : []});

  useEffect (() =>{

    fetch('http://localhost:8000/menu').then(
      response => response.json()
      ).then(data => {

        setItems({list : data})

    })}, [])






  return (
    <div className="App">

      <Order items = {items}/>



    </div>
  );
  }


export default App;
