import './App.css';
import React, {Component} from 'react';

class App extends Component {

  state = {

    items :{
      name : '',
      price : '',
    }
  };


  componentDidMount() {
    fetch('http://localhost:8000/menu').then(
      response => response.json()
      ).then(data => {this.setState({
        items : data
