import './App.css';
import React, {useState, useEffect} from 'react';

const App = () =>  {


  const [items, setItems] = useState({list : []});

  useEffect (() =>{

    fetch('http://localhost:8000/menu').then(
      response => response.json()
      ).then(data => {

        setItems({list : data})

    })}, [])






  return (
    <div className="App">

  {(items.list.map((item, index) => {
        return(
          <div key = {index}>
            <span>{item.itemName}</span>
           </div>
        )
      }))} 

    </div>
  );
  }


export default App;
