import './App.css';
import React, {useState, useEffect} from 'react';

function App()  {


  const [items, setItems] = useState(
    {
      name : '',
      price : ''
    }
  );

  useEffect (() =>{

    fetch('http://localhost:8000/menu').then(
      response => response.json()
      ).then(data => {
        setItems({data})
    })}, [])


  // componentDidMount() {

  // };

  // componentWillMount(){
  //   this.handleEvent();
  // }





  return (
    <div className="App">


       {items.map((item, index) => {
        return(
          <div key = {index}>
            <span>{item.title}</span>
            </div>
        )
      })} 

    </div>
  );
  }


export default App;
