import React, {useState, useEffect} from 'react';
// import {render} from 'react-dom';





const ViewOrder = () => {
  const [items, setItems] = useState({list : []});
  const [details, setDetails] = useState(false);
  const [id, setId] = useState();

  useEffect (() =>{

    fetch('http://localhost:8000/vieworder').then(
      response => response.json()
      ).then(data => {
        setItems({list : data})

    })}, [])

  const handleButtonClick = (e, id) => {
    // e.preventDefault();
    setId(id);
    setDetails(true);

    }
    if (!details) {
  return (

    <>
    {(items.list.map((item, index) => {
      return (
        <div key = {index}>
        <h3>{item.table.id}  {item.table.tableName}</h3>
        <button onClick = {(e) => handleButtonClick(e,index)}>View/Edit</button>

        </div>
      )


      }))}
      </>

  );}
  else{

    const [foods, setFoods] = useState({food : []})
    const [refresh , setRefresh] = useState(true)
    const [inputList, setInputList] = useState([{ itemid: "", quantity: "" }]);




      fetch('http://localhost:8000/menu').then(
        response => response.json()
        ).then(data => {
          setFoods({food : data})

      });

      fetch ('http://localhost:800/viewOrderedItem?id='+id).then(
        response => response.json()
      ).then(console.log(response))


    const handleInputChange = (e, index) => {
      const { name, value } = e.target;
      const list = [...inputList];
      list[index][name] = value;
      setInputList(list);
    };

    const handleAddClick = () => {
      setInputList([...inputList, { itemid: "", quantity: "" }]);
    };

    const handleSubmit = (e) => {
      e.preventDefault()

      let url = 'http://localhost:8000/createorder'


      fetch (url, {
        method : 'POST',
        headers : {
          'Content-type' : 'application/json',
        },
        body : JSON.stringify({
          'tid': e.target.table.value,
          'OrderedItem': inputList,
      })
      }).then ((response) => {
        console.log(response);
        if (response.ok){

        alert.show('Task was sucessful !!', {type:'success'})
        setRefresh (!refresh)

        setInputList ([{ itemid: "", quantity: "" }])
        }
        else{
          throw new Error("Not Found")
        }

      }).catch ((error) => {
        setRefresh (!refresh)
        alert.show('There was an error !!!' , {type : 'error'})
      })

    }


    return (

  <>
  <form onSubmit = {handleSubmit} >

      {inputList.map((x, i) => {
        return (
          <div className="box">
            <select name = "itemid" onChange = {e => handleInputChange(e, i)} value = {x.itemid || ''} >
              <option value = "">----</option>
              {items.foods.map(food =>
                <option key = {food.id} value = {food.id}> {food.itemName} </option>
              )}
            </select>

            <input type = "number" name = "quantity" key = {x + i} onChange = {e => handleInputChange(e, i)}
            value = {x.quantity} />


            <div className="btn-box">

              {inputList.length - 1 === i && <button onClick={handleAddClick}>Add</button>}
            </div>
          </div>
        );
      })}

  <input type = 'submit' />
  </form>


    </>

    );


  }
}


export default ViewOrder ;
