import React, {useEffect, useState} from 'react';
import { useAlert } from 'react-alert'

const AddOrder = () => {
  const [table, setTable] = useState({tables : []})
  const [items, setItems] = useState({foods : []})


  useEffect(() => {

    fetch('http://localhost:8000/menu').then(
      response => response.json()
      ).then(data => {
        console.log(data);
        setItems({foods : data})

    });

    fetch('http://localhost:8000/tables').then(
      response => response.json()
      ).then(data => {
        console.log(data);
        setTable({tables : data})

    })

  }, [])
  const handleSubmit = (e) => {
    e.preventDefault()

    let url = 'http://localhost:8000/createorder'

    fetch (url, {
      method : 'POST',
      headers : {
        'Content-type' : 'application/json',
      },
      body : JSON.stringify({
        'table': '',
        'OrderedItem': [
            {'item': 'Public Service Announcement', 'quantity': 245},

        ],
    })
    }).then ((response) => {
      alert.show('Task was sucessful !!', {type:'success'})
    }).catch ((error) => {
      alert.show('There was an error !!!', {type : 'error'})
    })
  }
  const alert = useAlert()

  return (
    <>

    <form onSubmit = {handleSubmit} >

      <Select name='table'>
        {table.map(tab =>
          <option key={tab.id} value={tab}>{tab.tableName}</option>
          )};
      </Select>

      {/* <label >name</label>
      <input type = 'text' name = 'name'/>
      <input type = 'number' name = 'price'/> */}

      <input type = 'submit' />
    </form>
    </>
  );

}

export default AddOrder;
